CREATE MEMORY TABLE "PUBLIC"."PROMO_CODES"(
    "CODE" CHARACTER VARYING(255) NOT NULL,
    "CURRENCY" CHARACTER VARYING(255),
    "DISCOUNT_VALUE" NUMERIC(38, 2),
    "EXPIRES" DATE,
    "HAS_BEEN_USED_TIMES" INTEGER,
    "MAXIMUM_USAGES" INTEGER
);     
ALTER TABLE "PUBLIC"."PROMO_CODES" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_BF" PRIMARY KEY("CODE");
