CREATE MEMORY TABLE "PUBLIC"."PURCHASES"(
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 3) NOT NULL,
    "CURRENCY" CHARACTER VARYING(255),
    "PURCHASE_DATE" DATE,
    "REGULAR_PRICE" NUMERIC(38, 2),
    "TOTAL_PRICE" NUMERIC(38, 2),
    "PRODUCT_ID" BIGINT,
    "PROMO_CODE_ID" CHARACTER VARYING(255)
);
ALTER TABLE "PUBLIC"."PURCHASES" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_3B" PRIMARY KEY("ID");    
